import React from 'react';
import { Link } from 'react-router-dom';
import appartementsData from '../data/appartements.json';

function Accueil() {
  return (
    <div>
      <h1>Appartements de vacances</h1>
      <ul>
        {appartementsData.map((appartement) => (
          <li key={appartement.id}>
            <Link to={`/appartement/${appartement.id}`}>
              <img src={appartement.cover} alt={appartement.title} />
              <h2>{appartement.title}</h2>
              <p>{appartement.location}</p>
              <p>Description: {appartement.description}</p>
              <p>Équipements: {appartement.equipments.join(', ')}</p>
              <p>Tags: {appartement.tags.join(', ')}</p>
              <p>Note: {appartement.rating}</p>
              <div>
                <img src={appartement.host.picture} alt={appartement.host.name} />
                <p>Propriétaire: {appartement.host.name}</p>
              </div>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Accueil;
