import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Accueil() {
  const [appartements, setAppartements] = useState([]);

  useEffect(() => {
    axios.get('https://votre-api.com/appartements')
      .then(response => {
        setAppartements(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données :', error);
      });
  }, []);

  return (
    <div>
      <h1>Appartements de vacances</h1>
      <ul>
        {appartements.map(appartement => (
          <li key={appartement.id}>
            <img src={appartement.cover} alt={appartement.title} />
            <h2>{appartement.title}</h2>
            <p>{appartement.description}</p>
            <a href={`/appartements/${appartement.id}`}>Voir plus</a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Accueil;
